mapper.select:
select re.id as "id", re.project_name as "projectNo", nvl(re.cost_name,'开发费') as "costType", re.cost_hours as "costHours", re.unit_cost as "unitCost", re.cost_hours * re.unit_cost as "money", to_char(re.work_date,'yyyy-MM-dd') as "workDate", nvl(re.work_add,'公司') as "workAdd", re.comments as "comments", re.log_name as "createUser", to_char(re.create_date,'yyyy-MM-dd') as "createDate" from (select row_.*, rownum rownum_ from (select f.*, p.project_name, c.cost_name,i.log_name from project_cost f, project p, cost_type c,log_id i where f.project_no = p.project_no and f.cost_type = c.cost_type(+) and f.create_user = i.log_id and f.create_user = #{createUser}
and f.project_no like '%${projectNo}%'
and f.cost_type = #{costType}
and f.work_add = '${workAdd}'
and to_char(f.work_date,'yyyy-MM-dd') = to_char(#{workDate, jdbcType=DATE},'yyyy-MM-dd')
order by f.work_date desc) row_ where rownum <= #{pageNum} * #{pageSize} ) re where rownum_ > (#{pageNum} - 1) * #{pageSize}

select count(*) from project_cost f where f.create_user = #{createUser}

    and f.project_no like '%${projectNo}%'


    and f.cost_type = #{costType}


    and f.work_add = '${workAdd}'


    and to_char(f.work_date,'yyyy-MM-dd') = to_char(#{workDate, jdbcType=DATE},'yyyy-MM-dd')
